  if (!recetaEnEstado) return;

  async function searchRecepie() {
    const search = searchInput.value;

    if(search) {
        if (radioRecepie.checked){
          var url = buildUrlReceta(search);
        }else if (radioIngredient.checked){
          var url = buildUrlIngrediente(search);
        }

      results.innerHTML = "";
      const recepies = await getRecepies(url);
          if (radioRecepie.checked){
      recepies.results.forEach(recepie => {
              results.appendChild(createCardRecepie(recepie))
      })
      }else if (radioIngredient.checked){
          recepies.forEach(recepie => {
              results.appendChild(createCardIngredients(recepie))
      })
      }
    }
    searchInput.value = ""; 
  }

};

  return (
    <div>
     <h1>Busque una receta</h1>
     <br></br> 
      <br></br>
