    <label>
        <input type="radio" id="ingrediente" name="busqueda" onChange={() => setBusqueda('ingrediente')}/> Ingrediente
    </label>
        </div>
      <input onChange={handleInputChange} value={receta}></input>
      <button onClick={handleClick}>
        Buscar
